export class DateValueConverter {
  toView(date: Date) {
    let t = typeof date;

    if (date) {
      if (t == 'number') {
        date = new Date(date)
        return this.f_d(date);
      } else if (t == 'string') {
        return date;
      } else if (t == 'object') {
        return this.f_d(date);
      }
    } else {
      return '';
    }
  }

  f_d(date: Date) {
    let y = String(date.getFullYear());
    let m = date.getMonth() + 1;
    let hh = date.getHours();
    let mm = date.getMinutes();
    let s_mm = mm < 10 ? "0" + mm.toString() : mm.toString();
    let d = date.getDate();
    let s = y + '/' + m + '/' + d + " " + hh + ":" + s_mm;

    return s;
  }
  twoDigitPad(num) {
    return num < 10 ? "0" + num : num;
  }
  fromView(x: string) {
    let date = new Date();
    let y = Number(x.substr(0, 4));
    let m = Number(x.substr(5, 2)) - 1;
    console.log("fasdfjlakdsjfljasdlfj" + m);

    let d = Number(x.substr(8, 2));
    // let h = Number(x.substr(11, 2));
    // let mm = Number(x.substr(14,2));
    date.setFullYear(y);
    date.setMonth(m, d);
    //date.setDate(d)
    // date.setHours(h);
    // date.setMinutes(mm);
    // date.setSeconds(0);
    console.log(date);
    return date;
  }
}
