import { TabRouter, TabPage } from './tab_router';

export class Tab {
    router: TabRouter;
    static inject() { return [TabRouter] }
    constructor(t, a) {
        this.router = t;
    }

    activate() {
        console.log("activate...");

    }
    attached() {
    }

    select_tab(tab: TabPage) {
        this.router.select(tab)
    }
    close_tab(tab: TabPage, evt:Event) {
        
        evt.stopPropagation();
        this.router.close(tab)
    }
}